                               &lt;tbody&gt;
                               &lt;tr&gt;
                               &lt;td scope=&quot;col&quot; style=&quot;width: 30px; text-align: center; vertical-align: top; padding-top: 5px; color: #F46B1B; font-weight: bold;&quot;&gt;&amp;lt;&lt;/td&gt;
